package health.ere.ps.service.connector;

import static org.junit.jupiter.api.Assertions.assertNotNull;
import static org.mockito.ArgumentMatchers.any;
import static org.mockito.Mockito.mock;
import static org.mockito.Mockito.times;
import static org.mockito.Mockito.verify;

import java.io.File;
import java.io.IOException;
import java.net.URISyntaxException;
import java.security.KeyStoreException;
import java.security.NoSuchAlgorithmException;
import java.security.cert.CertificateException;

import javax.net.ssl.KeyManager;

import org.junit.jupiter.api.Test;

import health.ere.ps.config.RuntimeConfig;
import health.ere.ps.config.UserConfig;
import health.ere.ps.service.connector.provider.SingleConnectorServicesProvider;

public class SingleConnectorServicesProviderTest {
    private String keystoreFileName = "src/test/resources/certs/keystore.p12";
    private String keystoreFilePass = "123456";
    
    @Test
    public void testConstructor() {
        UserConfig runtimeConfig = new RuntimeConfig();
        new SingleConnectorServicesProvider(runtimeConfig, null);
    }

    @Test
    public void testConstructorWithBaseUrl() {
        UserConfig runtimeConfig = new RuntimeConfig("http://localhost:123456/");
        new SingleConnectorServicesProvider(runtimeConfig, null);
    }

    @Test
    public void testConstructorWithMultiKeyWithoutPassword() {
        jakarta.enterprise.event.Event<Exception> exceptionEvent = mock(jakarta.enterprise.event.Event.class);
        UserConfig runtimeConfig = new RuntimeConfig();
        runtimeConfig.getConfigurations().setClientCertificate(new File(keystoreFileName).toURI().toString()+"?alias=key2");
        new SingleConnectorServicesProvider(runtimeConfig, exceptionEvent);
        verify(exceptionEvent).fireAsync(any());
    }

    @Test
    public void testConstructorWithMultiKeyWithPassword() {
        jakarta.enterprise.event.Event<Exception> exceptionEvent = mock(jakarta.enterprise.event.Event.class);
        UserConfig runtimeConfig = new RuntimeConfig();
        runtimeConfig.getConfigurations().setClientCertificate(new File(keystoreFileName).toURI().toString()+"?alias=key2");
        runtimeConfig.getConfigurations().setClientCertificatePassword(keystoreFilePass);
        new SingleConnectorServicesProvider(runtimeConfig, exceptionEvent);
        verify(exceptionEvent, times(0)).fireAsync(any());
    }

    @Test
    public void testGetKeyFromBase64DataUri() throws URISyntaxException, KeyStoreException, NoSuchAlgorithmException, CertificateException, IOException {
        String keystoreDataUri = "data:application/x-pkcs12;base64,";
        String keystorePass = "00";
        KeyManager km = SingleConnectorServicesProvider.getKeyFromKeyStoreUri(keystoreDataUri, keystorePass);
        assertNotNull(km);
    }

    @Test
    public void testGetKeyFromBase64OctetDataUri() throws URISyntaxException, KeyStoreException, NoSuchAlgorithmException, CertificateException, IOException {
        String keystoreDataUri = "data:application/octet-stream;base64,";
        String keystorePass = "00";
        KeyManager km = SingleConnectorServicesProvider.getKeyFromKeyStoreUri(keystoreDataUri, keystorePass);
        assertNotNull(km);
    }
    
    @Test
    public void testGetKeyFromFileUriWithoutAlias() throws URISyntaxException, KeyStoreException, NoSuchAlgorithmException, CertificateException, IOException {
        KeyManager km = SingleConnectorServicesProvider.getKeyFromKeyStoreUri(new File(keystoreFileName).toURI().toString(), keystoreFilePass);
        assertNotNull(km);
    }

    @Test
    public void testGetKeyFromFileUriWithAlias() throws URISyntaxException, KeyStoreException, NoSuchAlgorithmException, CertificateException, IOException {
        KeyManager km = SingleConnectorServicesProvider.getKeyFromKeyStoreUri(new File(keystoreFileName).toURI().toString()+"?alias=key2", keystoreFilePass);
        assertNotNull(km);
    }
}